package JavaBasic;

public class Star1234 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int r=4;
		int c=1;
		int n=1;
		
		for(int i=1;i<=r;i++) {
			for(int j=1;j<=c;j++) {
				System.out.print(n++);
				
			}
			System.out.println();
			c++;
			
		}
	}

}
