package Java;

public class Highest_Num {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=23;
		int b=44;
		int c=60;
		
		if(a>b && a>c) {
			System.out.println("a is greater" + a);
		}
		else if(b>c) {
			System.out.println("b is greater" + b);
		}
		else {
			System.out.println("c is greater" + c);
		}
		
	}

}
